import React, { useState } from 'react';
import './CategoryDisplay.css'; // Certifique-se de importar o CSS

function CategoryDisplay() {
    const [numbers, setNumbers] = useState({ A: 1, B: 1, C: 1, D: 1 });
    const colors = ['#5AA7FF', '#49C493', '#FFCB2F', '#FF5757']; // Array de cores

    const handleIncrement = (category) => {
        setNumbers(prev => ({...prev, [category]: prev[category] + 1 }));
    };

    // Função para calcular o índice da cor com base no número total de categorias
    const getColorIndex = () => {
        let index = Object.keys(numbers).length % colors.length;
        if (index < 0) index += colors.length; // Caso haja menos de 4 categorias
        return index;
    };

    return (
        <div>
            <h3>Categories:</h3>
            <div className="card-container-cat">
                {Object.entries(numbers).map(([category, number], index) => (
                    <div key={category} className={`card ${getColorIndex(index)}`}>
                        <span>{category}: {number}</span>
                        <button onClick={() => handleIncrement(category)} className="call-next-button">Call Next</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default CategoryDisplay;
