import React, { useState } from 'react';
import './ConsultationsTable.css';

function ConsultationsTable({ consultations }) {
    const [filter, setFilter] = useState('');

    const filteredConsultations = consultations.filter(consultation =>
        Object.values(consultation).some(value =>
            value.toString().toLowerCase().includes(filter.toLowerCase())
        )
    );

    function CharComponent({ char, searchTerm, isHighlighted }) {
        const style = isHighlighted? { backgroundColor: 'yellow' } : {}; // Defina a cor de destaque conforme necessário
      
        return <span style={style}>{char}</span>;
      }
      


    return (
        <div className="table-consultation-container">
            <input type="text" placeholder="Search..." className="table-consultation-search" onChange={e => setFilter(e.target.value)} />
            <button className="add-consultation-btn">Add Consultation</button>
            <table>
                <thead>
                    <tr>
                        <th>Number</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Time</th>
                        <th>Doctor</th>
                        <th>Specialty</th>
                        <th>Hospital</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredConsultations.map((item, index) => (
                        <tr key={index}>
                            <td>{item.number}</td>
                            <td>{item.name}</td>
                            <td>{item.date}</td>
                            <td>{item.time}</td>
                            <td>{item.doctor}</td>
                            <td>{item.specialty}</td>
                            <td>{item.hospital}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ConsultationsTable;
