import React, { useState } from 'react';
import './ConsultationsTable.css';

function ConsultationsTable({ consultations }) {
    const [filter, setFilter] = useState('');

    const filteredConsultations = consultations.filter(consultation =>
        Object.values(consultation).some(value =>
            value.toString().toLowerCase().includes(filter.toLowerCase())
        )
    );

    const [highlightIndex, setHighlightIndex] = useState(null);


    const handleSearchChange = (e) => {
        setFilter(e.target.value);
        // Supondo que você queira destacar o primeiro caractere que corresponde à pesquisa:
        const firstMatchIndex = item.name.toLowerCase().indexOf(filter.toLowerCase());
        if (firstMatchIndex!== -1) {
            setHighlightIndex(firstMatchIndex);
        } else {
            setHighlightIndex(null);
        }
    };
    
      
    function CharComponent({ char, searchTerm, isHighlighted }) {
        const style = isHighlighted? { backgroundColor: 'yellow' } : {};
    
        return <span style={style}>{char}</span>;
    }

    return (
        <div className="table-consultation-container">
            <input type="text" placeholder="Search..." className="table-consultation-search" onChange={e => setFilter(e.target.value)} />
            <button className="add-consultation-btn">Add Consultation</button>
            <table>
                <thead>
                    <tr>
                        <th>Number</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Time</th>
                        <th>Doctor</th>
                        <th>Specialty</th>
                        <th>Hospital</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredConsultations.map((item, index) => (
                        <tr key={index}>
                            <td>{item.number}</td>
                            <td>{item.name}</td>
                            <td>{item.date}</td>
                            <td>{item.time}</td>
                            <td>{item.doctor}</td>
                            <td>{item.specialty}</td>
                            <td>{item.hospital}</td>
                        </tr>
                    ))}
                </tbody>
                <tbody>
                    {filteredConsultations.map((item, index) => (
                        <tr key={index}>
                            <td>
                                {Array.from(item.name).map((char, i) => (
                                    <CharComponent
                                        key={i}
                                        char={char}
                                        searchTerm={filter}
                                        isHighlighted={i === highlightIndex}
                                    />
                                ))}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ConsultationsTable;
